mapper.select:
select member_no as memberNo, member_id as memberId, member_nickname as memberNickname, member_phone as memberPhone, member_city as memberCity, member_photo as memberPhoto, member_intro as memberIntro, enroll_date as enrollDate, member_level as memberLevel, join_type as joinType from member_tbl where member_id = #{memberId}
and member_pw = #{memberPw}
and member_phone = #{memberPhone}

mapper.update:
update member_tbl
set member_pw = #{memberPw}
where member_id = #{memberId}
